def run_ultimate(words):
    random.shuffle(words)
    ultimate_choice = ''
    for i in range(len(words)):
        ultimate_choice += words[i]
        if i<len(words)-1:
            ultimate_choice += ','

        
    print(ultimate_choice)
    play(ultimate_choice)

